import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { FaSearch } from "react-icons/fa";
import logo from "./assets/sooraj-logo.PNG";
import Home from "./pages/Home";
import About from "./pages/About";
import Contact from "./pages/Contact";
import FAQs from "./pages/Faqs";
import Products from "./pages/Products";
import Footer from "./components/Footer";
import "./App.css";

function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [search, setSearch] = useState("");

  return (
    <Router>
      <header className="header">
        <div className="logo-section">
          <img src={logo} alt="Sooraj Logo" className="logo" />
          <h1 className="site-title">Sooraj Crop Sciences</h1>
        </div>

        <nav
          className="nav-links"
          style={{ display: "flex", alignItems: "center", gap: "20px" }}
        >
          <Link to="/" className="nav-link">Home</Link>
          <Link to="/about" className="nav-link">About</Link>
          <Link to="/Products" className="nav-link">Products</Link>
          <Link to="/contact" className="nav-link">Contact</Link>
          <Link to="/FAQs" className="nav-link">FAQs</Link>

          {/* üîç Search Icon */}
          <div style={{ display: "flex", alignItems: "center", position: "relative" }}>
            <div
              onClick={() => setIsOpen((prev) => !prev)}
              style={{
                backgroundColor: "#127a3a",
                color: "white",
                padding: "8px",
                borderRadius: "50%",
                cursor: "pointer",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                transition: "all 0.3s ease",
              }}
              title="Search products"
            >
              <FaSearch size={16} />
            </div>

            <input
              type="text"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              placeholder="Search products..."
              style={{
                padding: "8px 12px",
                width: isOpen ? "200px" : "0px",
                opacity: isOpen ? 1 : 0,
                border: isOpen ? "1px solid #ccc" : "none",
                borderRadius: "6px",
                outline: "none",
                marginLeft: "6px",
                transition: "all 0.4s ease",
              }}
            />
          </div>
        </nav>
      </header>

      {/* ‚úÖ Pass search props to Home */}
      <Routes>
        <Route path="/" element={<Home search={search} setSearch={setSearch} />} />
        <Route path="/about" element={<About />} />
        <Route path="/Products" element={<Products />} />
        <Route path="/contact" element={<Contact />} />
        <Route path="/FAQs" element={<FAQs />} />
      </Routes>

      <Footer />
    </Router>
  );
}

export default App;
