// src/components/Hero.jsx
import React, { useState } from "react";
import { FaSearch } from "react-icons/fa"; // search icon

export default function Hero({ search, setSearch }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <section
      style={{
        background: "rgba(18,122,58,0.08)",
        padding: "60px 0",
        textAlign: "center",
        transition: "all 0.3s ease",
      }}
    >
      <h2 style={{ color: "#127a3a", marginBottom: 10 }}>
        Welcome to Sooraj Crop Sciences
      </h2>
      <p style={{ color: "#555", marginBottom: 20 }}>
        Search our agricultural products
      </p>

      {/* Search Container */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          gap: "10px",
        }}
      >
        {/* Search Icon */}
        <div
          onClick={() => setIsOpen((prev) => !prev)}
          style={{
            backgroundColor: "#127a3a",
            color: "white",
            padding: "10px",
            borderRadius: "50%",
            cursor: "pointer",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            transition: "all 0.3s ease",
          }}
          title="Search products"
        >
          <FaSearch size={18} />
        </div>

        {/* Expanding Search Input */}
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Search products..."
          style={{
            padding: "10px 14px",
            width: isOpen ? "280px" : "0px",
            opacity: isOpen ? 1 : 0,
            border: isOpen ? "1px solid #ccc" : "none",
            borderRadius: "8px",
            outline: "none",
            transition: "all 0.4s ease",
          }}
        />
      </div>
    </section>
  );
}
