import React from "react";
import Hero from "../components/Hero";
import ProductCard from "../components/ProductCard";
import products from "../data/products";

export default function Home({ search, setSearch }) {
  // Select only the first 8 featured products
  const featuredProducts = products.slice(0, 8);
  const filtered = products.filter((p) =>
    p.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
       <Hero search={search} setSearch={setSearch} />
       
       <section style={{ padding: "40px 0", width: "80%", margin: "0 auto" }}>
        <h2 style={{ color: "#127a3a", marginBottom: 20 }}>
          Featured Products
        </h2>

        {filtered.length === 0 ? (
          <p style={{ color: "#666" }}>No products found.</p>
        ) : (
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
              gap: 20,
            }}
          >
            {filtered.map((p) => (
              <ProductCard key={p.id} product={p} />
            ))}
          </div>
        )}
      </section>

      {/* üåæ Featured Products Section */}
      <section
        style={{
          padding: "30px 0",
          width: "85%",
          margin: "0 auto",
          textAlign: "center",
        }}
      >
        <h2
          style={{
            color: "#127a3a",
            marginBottom: "30px",
            fontSize: "1.8rem",
            fontWeight: "700",
            letterSpacing: "0.5px",
          }}
        >
          üåø Featured Products
        </h2>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(240px, 1fr))",
            gap: "18px", //height
            justifyContent: "center",
          }}
        >
          {featuredProducts.map((p) => (
            <ProductCard key={p.id} product={p} />
          ))}
        </div>

        {/* Optional ‚ÄúView All‚Äù Button */}
        <div style={{ marginTop: "40px" }}>
          <a
            href="/products"
            style={{
              background: "#127a3a",
              color: "#fff",
              padding: "12px 28px",
              borderRadius: "6px",
              textDecoration: "none",
              fontWeight: 600,
              transition: "background 0.3s ease",
            }}
            onMouseEnter={(e) => (e.currentTarget.style.background = "#0e6230")}
            onMouseLeave={(e) => (e.currentTarget.style.background = "#127a3a")}
          >
            View All Products
          </a>
        </div>
      </section>
    </div>
  );
}
